package com.class8;

public class LoopsCounter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String word="Mirror";
int rCount=0;
for(int i=0; i<word.length();i++) {
	if (word.charAt(i)=='r') {
		rCount++;
		System.out.println("Your word has " +rCount+"r(s) in it.");
	}
}
	
	
	
	
	
	}

}
